var viewButton=document.querySelector(".form-view-button"),modal=document.querySelector(".hostel-search-form");viewButton.addEventListener("click",function(o){o.preventDefault(),modal.classList.contains("modal-hide")?(modal.classList.remove("modal-hide"),modal.classList.add("modal-show")):modal.classList.contains("modal-show")&&(modal.classList.remove("modal-show"),modal.classList.add("modal-hide"))});